// Helper function to validate image URL
function isValidImageUrl(url) {
  if (!url) return false;

  const urlLower = url.toLowerCase().trim();

  const invalidKeywords = ['aparat', 'logo', 'icon', 'banner', 'avatar', 'placeholder'];
  const videoExtensions = ['.mp4', '.webm', '.avi', '.mov', '.flv', '.mkv', '.m4v', '.3gp'];
  const validImageExtensions = ['.jpg', '.jpeg', '.png', '.gif', '.webp', '.svg'];

  const hasVideoExtension = videoExtensions.some(ext => urlLower.includes(ext));
  const hasInvalidKeyword = invalidKeywords.some(k => urlLower.includes(k));
  const hasValidImageExtension = validImageExtensions.some(ext => urlLower.endsWith(ext));

  return !hasVideoExtension && !hasInvalidKeyword && hasValidImageExtension;
}

// Helper function to parse srcset and get largest image
function parseSrcset(srcsetString) {
  if (!srcsetString) return null;

  const pairs = [...srcsetString.matchAll(/(\S+)\s+(\d+)w/g)];
  if (pairs.length > 0) {
    pairs.sort((a, b) => parseInt(b[2]) - parseInt(a[2]));
    return pairs[0][1].trim();
  }
  return null;
}

// Helper function to normalize URL (relative to absolute)
function normalizeUrl(url, baseUrl) {
  if (!url) return null;

  url = url.trim();

  // Already absolute
  if (url.startsWith('http://') || url.startsWith('https://')) {
    return url;
  }

  // Make absolute
  try {
    const base = new URL(baseUrl);
    return new URL(url, baseUrl).href;
  } catch (e) {
    return url; // Return as-is if URL parsing fails
  }
}

