// Get Latest RSS Item Only
const items = $input.all();

// ŸÅŸÇÿ∑ ÿßŸàŸÑ€åŸÜ ÿ¢€åÿ™ŸÖ (ÿ¢ÿÆÿ±€åŸÜ ÿÆÿ®ÿ±) ÿ±ÿß ÿ®ÿ±ŸÖ€å‚Äå⁄Øÿ±ÿØÿßŸÜ€åŸÖ
if (items.length > 0) {
  return [items[0]];
}

return [];

// Decode Link and Validate
const items = $input.all();
const validItems = [];

for (const item of items) {
  const json = item.json;

  // Get and validate link
  let link = json.link || '';
  link = link.trim();

  // Skip items without valid link
  if (!link || link.length < 10 || (!link.startsWith('http://') && !link.startsWith('https://'))) {
    console.log('Skipping item with invalid link:', link);
    continue;
  }

  // Decode URL-encoded characters
  try {
    link = decodeURIComponent(link);
  } catch (e) {
    // keep original if decode fails
  }

  // Keep original data + decoded link
  validItems.push({
    json: {
      ...json,
      link: link,
      originalTitle: json.title || 'ÿ®ÿØŸàŸÜ ÿπŸÜŸàÿßŸÜ'
    }
  });
}

return validItems;

// Decode ALL fields immediately after HTML extraction
const items = $input.all();
const outputItems = [];

for (const item of items) {
  const json = item.json;

  // ===== DECODE TITLE =====
  let title = json.title || '';
  if (title) {
    // ÿß⁄Øÿ± ÿ¢ÿ±ÿß€åŸá ÿßÿ≥ÿ™ÿå join ⁄©ŸÜ
    if (Array.isArray(title)) {
      title = title.join(' ');
    }
    title = String(title);

    // ÿ≠ÿßŸÑÿß decode ⁄©ŸÜ - ⁄ÜŸÜÿØ€åŸÜ ÿ®ÿßÿ± ÿß⁄Øÿ± ŸÑÿßÿ≤ŸÖ ÿ®ÿßÿ¥ÿØ
    let decoded = title;
    for (let i = 0; i < 3; i++) {
      try {
        const temp = decodeURIComponent(decoded);
        if (temp === decoded) break; // ÿß⁄Øÿ± ÿ™ÿ∫€å€åÿ±€å ŸÜ⁄©ÿ±ÿØÿå ÿ®€åÿ±ŸàŸÜ ÿ®€åÿß
        decoded = temp;
      } catch (e) {
        break;
      }
    }
    title = decoded;
  }

  // ===== DECODE CONTENT =====
  let content = json.content || '';
  if (content) {
    // ÿß⁄Øÿ± ÿ¢ÿ±ÿß€åŸá ÿßÿ≥ÿ™ÿå join ⁄©ŸÜ
    if (Array.isArray(content)) {
      content = content.join('\n\n');
    }
    content = String(content);

    // decode ⁄ÜŸÜÿØÿ®ÿßÿ±Ÿá
    let decoded = content;
    for (let i = 0; i < 3; i++) {
      try {
        const temp = decodeURIComponent(decoded);
        if (temp === decoded) break;
        decoded = temp;
      } catch (e) {
        break;
      }
    }
    content = decoded;
  }

  // ===== DECODE IMAGE URL =====
  let imageUrl = json.imageUrl || '';
  if (imageUrl) {
    if (Array.isArray(imageUrl)) {
      imageUrl = imageUrl[0] || '';
    }
    imageUrl = String(imageUrl).trim();

    // decode URL
    try {
      const decoded = decodeURIComponent(imageUrl);
      imageUrl = decoded;
    } catch (e) {
      // ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿßÿ≤ URL ÿßÿµŸÑ€å
    }
  }

  // ===== OUTPUT =====
  outputItems.push({
    json: {
      ...json,
      title: title,
      content: content,
      imageUrl: imageUrl,
      originalTitle: title
    }
  });
}

return outputItems;

// Prepare Gemini Request - ÿ®ÿß Ÿæÿ¥ÿ™€åÿ®ÿßŸÜ€å ⁄©ÿßŸÖŸÑ ÿßÿ≤ Binary Ÿà String
const items = $input.all();
const outputItems = [];

for (const item of items) {
  // ===== ÿØÿ±€åÿßŸÅÿ™ ÿπŸÜŸàÿßŸÜ =====
  const title = item.json.title || 'ÿ®ÿØŸàŸÜ ÿπŸÜŸàÿßŸÜ';

  // ===== ÿØÿ±€åÿßŸÅÿ™ ŸÖÿ≠ÿ™Ÿàÿß =====
  let content = item.json.content || '';

  // ÿß⁄Øÿ± ŸÖÿ≠ÿ™Ÿàÿß ÿ¢ÿ±ÿß€åŸá ÿßÿ≥ÿ™ÿå ÿ®Ÿá ÿ±ÿ¥ÿ™Ÿá ÿ™ÿ®ÿØ€åŸÑ ⁄©ŸÜ
  if (Array.isArray(content)) {
    content = content.join('\n\n');
  }

  // Ÿæÿß⁄© ⁄©ÿ±ÿØŸÜ ÿ™⁄Ø‚ÄåŸáÿß€å HTML
  let contentString = String(content).replace(/<[^>]*>/g, '');

  // ŸÖÿ≠ÿØŸàÿØ ⁄©ÿ±ÿØŸÜ ÿ∑ŸàŸÑ
  if (contentString.length > 3000) {
    contentString = contentString.substring(0, 3000) + '...';
  }

  // ===== ÿØÿ±€åÿßŸÅÿ™ URL ÿπ⁄©ÿ≥ ÿ®ÿß Ÿæÿ¥ÿ™€åÿ®ÿßŸÜ€å Binary =====
  let imageUrl = '';

  // ÿ±Ÿàÿ¥ 1: ⁄Ü⁄© ⁄©ÿ±ÿØŸÜ json.imageUrl (ÿ≠ÿßŸÑÿ™ ŸÖÿπŸÖŸàŸÑ€å)
  if (item.json.imageUrl) {
    const imgUrl = item.json.imageUrl;

    // ÿßÿ∑ŸÖ€åŸÜÿßŸÜ ÿßÿ≤ ÿß€åŸÜ⁄©Ÿá string ÿßÿ≥ÿ™
    if (typeof imgUrl === 'string') {
      imageUrl = imgUrl.trim();
    } else if (typeof imgUrl === 'object' && imgUrl.url) {
      // ÿß⁄Øÿ± object ÿ®ÿßÿ¥ÿØ ÿ®ÿß ŸÅ€åŸÑÿØ url
      imageUrl = imgUrl.url.trim();
    }
  }

  // ÿ±Ÿàÿ¥ 2: ⁄Ü⁄© ⁄©ÿ±ÿØŸÜ binary data (ÿß⁄Øÿ± ÿπ⁄©ÿ≥ ÿ®Ÿá ÿµŸàÿ±ÿ™ binary ÿ¢ŸæŸÑŸàÿØ ÿ¥ÿØŸá)
  let hasBinaryImage = false;
  if (item.binary && item.binary.data) {
    hasBinaryImage = true;
    console.log('Binary image detected');
  }

  // ÿßÿπÿ™ÿ®ÿßÿ±ÿ≥ŸÜÿ¨€å URL
  if (imageUrl && !imageUrl.startsWith('http://') && !imageUrl.startsWith('https://')) {
    console.log('Invalid image URL format:', imageUrl);
    imageUrl = '';
  }

  // ===== ÿ≥ÿßÿÆÿ™ Prompt =====
  const prompt = `ÿ¥ŸÖÿß €å⁄© ÿÆŸÑÿßÿµŸá‚ÄåŸÜŸà€åÿ≥ ÿ≠ÿ±ŸÅŸá‚Äåÿß€å Ÿáÿ≥ÿ™€åÿØ. ŸÑÿ∑ŸÅÿßŸã ÿÆŸÑÿßÿµŸá‚Äåÿß€å ÿ¨ÿ∞ÿßÿ®ÿå ÿÆŸàÿßŸÜÿß Ÿà ŸÖÿπŸÜÿßÿØÿßÿ± ÿßÿ≤ ŸÖŸÇÿßŸÑŸá ÿ≤€åÿ± ÿ®Ÿá ÿ≤ÿ®ÿßŸÜ ŸÅÿßÿ±ÿ≥€å ÿ®ŸÜŸà€åÿ≥€åÿØ.

ÿπŸÜŸàÿßŸÜ: ${title}

ŸÖÿ≠ÿ™Ÿàÿß: ${contentString}

${hasBinaryImage || imageUrl ? 'ÿ™ÿµŸà€åÿ± ŸÖÿ±ÿ™ÿ®ÿ∑ ÿ®ÿß ÿÆÿ®ÿ± ŸÜ€åÿ≤ ÿ∂ŸÖ€åŸÖŸá ÿ¥ÿØŸá ÿßÿ≥ÿ™.\n' : ''}
ÿÆŸÑÿßÿµŸá ÿ®ÿß€åÿØ:
- ÿ®€åŸÜ 200 ÿ™ÿß 400 ⁄©ÿßÿ±ÿß⁄©ÿ™ÿ± ÿ®ÿßÿ¥ÿØ
- ŸÜ⁄©ÿßÿ™ ŸÖŸáŸÖ Ÿà ⁄©ŸÑ€åÿØ€å ŸÖŸÇÿßŸÑŸá ÿ±ÿß ÿ¥ÿßŸÖŸÑ ÿ¥ŸàÿØ
- ÿ®ÿ±ÿß€å ŸÖÿÆÿßÿ∑ÿ® ÿ¨ÿ∞ÿßÿ® Ÿà ÿÆŸàÿßŸÜÿØŸÜ€å ÿ®ÿßÿ¥ÿØ
- ÿ®Ÿá ÿ≤ÿ®ÿßŸÜ ŸÅÿßÿ±ÿ≥€å ÿ±ŸàÿßŸÜ Ÿà ÿ≥ÿßÿØŸá ÿ®ÿßÿ¥ÿØ

ŸÅŸÇÿ∑ ÿÆŸÑÿßÿµŸá ÿ±ÿß ÿ®ŸÜŸà€åÿ≥€åÿØÿå ÿ®ÿØŸàŸÜ Ÿá€å⁄Ü ÿ™Ÿàÿ∂€åÿ≠ ÿßÿ∂ÿßŸÅ€å.`;

  // ===== ÿ≥ÿßÿÆÿ™ Body ÿ®ÿ±ÿß€å Gemini =====
  const geminiBody = {
    contents: [{
      parts: [{
        text: prompt
      }]
    }]
  };

  // ===== ÿÆÿ±Ÿàÿ¨€å =====
  outputItems.push({
    json: {
      ...item.json,
      imageUrl: imageUrl,
      hasBinaryImage: hasBinaryImage,
      geminiRequestBody: geminiBody,
      promptText: prompt
    },
    // ‚úÖ ÿ≠ŸÅÿ∏ binary ÿß⁄Øÿ± Ÿàÿ¨ŸàÿØ ÿØÿßÿ¥ÿ™Ÿá ÿ®ÿßÿ¥ÿØ
    binary: item.binary || {}
  });
}

return outputItems;

// Extract AI Summary - data is already decoded
const items = $input.all();
const outputItems = [];

for (const item of items) {
  const json = item.json;
  let aiSummary = '';

  try {
    const candidate = json.candidates?.[0];
    const text = candidate?.content?.parts?.[0]?.text;

    if (text) {
      aiSummary = text.trim();
    }
  } catch (e) {
    console.log('Error extracting AI summary:', e.message);
  }

  // Fallback
  if (!aiSummary) {
    let contentString = json.content || '';
    aiSummary = contentString.substring(0, 400);
    if (contentString.length > 400) {
      aiSummary += '...';
    }
  }

  outputItems.push({
    json: {
      ...json,
      aiSummary: aiSummary
    },
    // ‚úÖ ÿ≠ŸÅÿ∏ Binary Data
    binary: item.binary || {}
  });
}

return outputItems;

// Format Telegram Message - ÿ®ÿß ÿ≠ŸÅÿ∏ Binary Data
const items = $input.all();
const outputItems = [];

for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const json = item.json;

  const title = json.title || 'ÿ®ÿØŸàŸÜ ÿπŸÜŸàÿßŸÜ';
  const link = json.link || '';
  let imageUrl = json.imageUrl || '';

  // ÿßÿπÿ™ÿ®ÿßÿ±ÿ≥ŸÜÿ¨€å URL ÿπ⁄©ÿ≥
  if (imageUrl) {
    imageUrl = imageUrl.trim().replace(/[\r\n\t]/g, '');
    if (imageUrl && !imageUrl.startsWith('http://') && !imageUrl.startsWith('https://')) {
      console.log('Invalid image URL:', imageUrl);
      imageUrl = '';
    }
  }

  let summary = json.aiSummary || '';
  if (!summary) {
    let contentString = json.content || '';
    summary = contentString.substring(0, 400);
    if (contentString.length > 400) {
      summary += '...';
    }
  }

  // ÿ≥ÿßÿÆÿ™ ⁄©Ÿæÿ¥ŸÜ
  let caption = 'üì∞ ' + title + '\n\n' + summary + '\n\nüîó ÿßÿØÿßŸÖŸá ŸÖÿ∑ŸÑÿ® ÿØÿ± Ÿàÿ®ÿ≥ÿß€åÿ™: ' + link + '\n\nüì° @w_e_s_h';

  if (caption.length > 950) {
    const overhead = title.length + link.length + 100;
    const maxSummaryLength = 950 - overhead;
    summary = summary.substring(0, maxSummaryLength) + '...';
    caption = 'üì∞ ' + title + '\n\n' + summary + '\n\nüîó ÿßÿØÿßŸÖŸá ŸÖÿ∑ŸÑÿ® ÿØÿ± Ÿàÿ®ÿ≥ÿß€åÿ™: ' + link + '\n\nüì° @w_e_s_h';
  }

  // ‚úÖ ÿ®ÿ±ÿ±ÿ≥€å Ÿàÿ¨ŸàÿØ Binary Image
  const hasBinaryImage = Boolean(item.binary && item.binary.data);
  const hasImageUrl = Boolean(imageUrl && imageUrl.length > 0);
  const hasImage = hasBinaryImage || hasImageUrl;

  console.log('Image status:', {
    hasBinaryImage,
    hasImageUrl,
    hasImage,
    binaryKeys: item.binary ? Object.keys(item.binary) : []
  });

  // ‚úÖ ÿÆÿ±Ÿàÿ¨€å ÿ®ÿß ÿ≠ŸÅÿ∏ Binary Data
  outputItems.push({
    json: {
      title: title,
      link: link,
      imageUrl: imageUrl,
      caption: caption,
      aiSummary: summary,
      hasBinaryImage: hasBinaryImage,
      hasImageUrl: hasImageUrl,
      hasImage: hasImage,
      chatId: json.chatId || '-1003140416257'
    },
    binary: item.binary || {},  // ‚úÖ ÿ≠ŸÅÿ∏ Binary Data
    pairedItem: i  // ‚úÖ ÿ≠ŸÅÿ∏ Item Linking
  });
}

return outputItems;
