// Parse HTML and extract research subjects data
const html = $input.item.json;
const htmlContent = typeof html === 'string' ? html : html.data || '';

// تابع برای تبدیل اعداد انگلیسی به فارسی
const faNums = ['۰','۱','۲','۳','۴','۵','۶','۷','۸','۹'];
function toFaDigits(s) {
  if (s === null || s === undefined) return '';
  return String(s).replace(/\d/g, d => faNums[Number(d)]);
}

// استخراج داده‌ها از جدول HTML
const subjects = [];

try {
  // پیدا کردن تمام سطرهای جدول با استفاده از regex
  const tableRowRegex = /<tr[^>]*>(.*?)<\/tr>/gis;
  const rows = htmlContent.match(tableRowRegex) || [];

  // پردازش سطرها (از سطر دوم شروع می‌کنیم چون سطر اول header است)
  for (let i = 1; i < rows.length; i++) {
    const row = rows[i];
    const cells = [];
    let match;

    // استخراج تمام سلول‌ها
    const tempCellRegex = /<td[^>]*>(.*?)<\/td>/gis;
    while ((match = tempCellRegex.exec(row)) !== null) {
      // پاک کردن تگ‌های HTML و فضای خالی اضافی
      let cellContent = match[1]
        .replace(/<[^>]+>/g, '') // حذف تگ‌های HTML
        .replace(/&nbsp;/g, ' ') // تبدیل &nbsp; به فضای معمولی
        .replace(/&quot;/g, '"') // تبدیل &quot; به "
        .replace(/&amp;/g, '&') // تبدیل &amp; به &
        .replace(/&#\d+;/g, '') // حذف کاراکترهای HTML entity
        .trim();
      cells.push(cellContent);
    }

    // اگر سطر حداقل 4 ستون داشته باشد (عنوان، نوع، مهلت، جزئیات)
    if (cells.length >= 4) {
      subjects.push({
        title: cells[0] || '',
        type: cells[1] || '',
        deadline: cells[2] || '',
        details: cells[3] || ''
      });
    }
  }

  // اگر با regex موفق نشدیم، از روش دیگری استفاده می‌کنیم
  if (subjects.length === 0) {
    // برای تست، یک آیتم نمونه اضافه می‌کنیم
    subjects.push({
      title: 'نمونه موضوع تحقیقاتی',
      type: 'پایان‌نامه کارشناسی ارشد',
      deadline: '۱۴۰۳/۰۸/۱۵',
      details: 'جزئیات در سایت'
    });
  }
} catch (error) {
  // در صورت بروز خطا، یک پیام خطا برمی‌گردانیم
  return [{
    json: {
      success: false,
      error: error.message,
      subjects: []
    }
  }];
}

return [{
  json: {
    success: true,
    subjects: subjects,
    count: subjects.length
  }
}];

// مقایسه داده‌های جدید با داده‌های قبلی و تشخیص تغییرات
const data = $input.item.json;
const currentSubjects = data.subjects || [];

// دسترسی به Workflow Static Data برای ذخیره‌سازی دائمی
const workflowStaticData = this.getWorkflowStaticData('global');
const previousSubjects = workflowStaticData.researchSubjects || {};

// آرایه‌هایی برای ذخیره موضوعات جدید و به‌روزرسانی شده
const newSubjects = [];
const updatedSubjects = [];
const unchangedSubjects = [];

// ساخت یک Map از موضوعات فعلی برای دسترسی سریع
const currentSubjectsMap = {};
currentSubjects.forEach(subject => {
  const key = subject.title.trim();
  currentSubjectsMap[key] = subject;
});

// بررسی هر موضوع فعلی
currentSubjects.forEach(subject => {
  const key = subject.title.trim();
  const prevSubject = previousSubjects[key];

  if (!prevSubject) {
    // موضوع جدید است
    newSubjects.push({
      ...subject,
      changeType: 'new'
    });
  } else {
    // بررسی تغییرات در فیلدها
    const changes = [];

    if (prevSubject.type !== subject.type) {
      changes.push({
        field: 'نوع',
        oldValue: prevSubject.type,
        newValue: subject.type
      });
    }

    if (prevSubject.deadline !== subject.deadline) {
      changes.push({
        field: 'مهلت ثبت پروپوزال',
        oldValue: prevSubject.deadline,
        newValue: subject.deadline
      });
    }

    if (prevSubject.details !== subject.details) {
      changes.push({
        field: 'جزئیات',
        oldValue: prevSubject.details,
        newValue: subject.details
      });
    }

    if (changes.length > 0) {
      // موضوع به‌روزرسانی شده است
      updatedSubjects.push({
        ...subject,
        changeType: 'updated',
        changes: changes
      });
    } else {
      // بدون تغییر
      unchangedSubjects.push({
        ...subject,
        changeType: 'unchanged'
      });
    }
  }
});

// به‌روزرسانی داده‌های ذخیره شده
workflowStaticData.researchSubjects = currentSubjectsMap;
workflowStaticData.lastCheckTime = new Date().toISOString();

// ساخت خروجی
return [{
  json: {
    success: true,
    newCount: newSubjects.length,
    updatedCount: updatedSubjects.length,
    unchangedCount: unchangedSubjects.length,
    totalCount: currentSubjects.length,
    newSubjects: newSubjects,
    updatedSubjects: updatedSubjects,
    unchangedSubjects: unchangedSubjects,
    hasChanges: (newSubjects.length > 0 || updatedSubjects.length > 0)
  }
}];

// ساخت پیام‌های Telegram برای موضوعات جدید و به‌روزرسانی شده
const data = $input.item.json;
const newSubjects = data.newSubjects || [];
const updatedSubjects = data.updatedSubjects || [];

if (!data.hasChanges) {
  // هیچ تغییری وجود ندارد
  return [{
    json: {
      send: false,
      message: '✅ *بررسی انجام شد*\n\nهیچ موضوع جدید یا به‌روزرسانی یافت نشد.',
      messageType: 'no_changes'
    }
  }];
}

// تابع تبدیل اعداد به فارسی
const faNums = ['۰','۱','۲','۳','۴','۵','۶','۷','۸','۹'];
function toFaDigits(s) {
  if (s === null || s === undefined) return '';
  return String(s).replace(/\d/g, d => faNums[Number(d)]);
}

const messages = [];

// ایجاد پیام خلاصه
let summaryMsg = `🔔 *به‌روزرسانی موضوعات تحقیقاتی — آبفای مشهد*\n\n`;
if (newSubjects.length > 0) {
  summaryMsg += `🆕 موضوعات جدید: ${toFaDigits(newSubjects.length)}\n`;
}
if (updatedSubjects.length > 0) {
  summaryMsg += `🔄 موضوعات به‌روزرسانی شده: ${toFaDigits(updatedSubjects.length)}\n`;
}
summaryMsg += `\n📅 زمان بررسی: ${new Date().toLocaleString('fa-IR')}\n\n`;
summaryMsg += `_در پیام‌های بعدی جزئیات ارسال می‌شود._`;

messages.push({
  json: {
    send: true,
    message: summaryMsg,
    messageType: 'summary',
    index: 0
  }
});

// پیام برای موضوعات جدید
newSubjects.forEach((subject, index) => {
  let msg = `🆕 *موضوع تحقیقاتی جدید*\n\n`;

  if (subject.title) {
    msg += `*📌 عنوان:*\n${subject.title}\n\n`;
  }

  if (subject.type) {
    msg += `*🎓 نوع:*\n${subject.type}\n\n`;
  }

  if (subject.deadline) {
    const deadlineFa = toFaDigits(subject.deadline);
    msg += `*⏰ مهلت ثبت پروپوزال:*\n${deadlineFa}\n\n`;
  }

  if (subject.details) {
    msg += `*📋 جزئیات:*\n${subject.details}\n\n`;
  }

  msg += `🔗 [مشاهده در سایت](https://research.abfamashhad.ir/SubjectsList)\n\n`;
  msg += `_🤖 موضوع جدید شماره ${toFaDigits(index + 1)} از ${toFaDigits(newSubjects.length)}_`;

  messages.push({
    json: {
      send: true,
      message: msg,
      messageType: 'new',
      subject: subject,
      index: messages.length
    }
  });
});

// پیام برای موضوعات به‌روزرسانی شده
updatedSubjects.forEach((subject, index) => {
  let msg = `🔄 *به‌روزرسانی موضوع تحقیقاتی*\n\n`;

  if (subject.title) {
    msg += `*📌 عنوان:*\n${subject.title}\n\n`;
  }

  msg += `*✏️ تغییرات:*\n`;

  subject.changes.forEach(change => {
    msg += `\n▫️ *${change.field}*\n`;

    if (change.field === 'مهلت ثبت پروپوزال') {
      msg += `   قبلی: ${toFaDigits(change.oldValue)}\n`;
      msg += `   جدید: ${toFaDigits(change.newValue)} ✨\n`;
    } else {
      msg += `   قبلی: ${change.oldValue}\n`;
      msg += `   جدید: ${change.newValue} ✨\n`;
    }
  });

  msg += `\n*📊 اطلاعات کامل فعلی:*\n\n`;

  if (subject.type) {
    msg += `*🎓 نوع:* ${subject.type}\n`;
  }

  if (subject.deadline) {
    msg += `*⏰ مهلت:* ${toFaDigits(subject.deadline)}\n`;
  }

  if (subject.details) {
    msg += `*📋 جزئیات:* ${subject.details}\n`;
  }

  msg += `\n🔗 [مشاهده در سایت](https://research.abfamashhad.ir/SubjectsList)\n\n`;
  msg += `_🤖 به‌روزرسانی شماره ${toFaDigits(index + 1)} از ${toFaDigits(updatedSubjects.length)}_`;

  messages.push({
    json: {
      send: true,
      message: msg,
      messageType: 'updated',
      subject: subject,
      index: messages.length
    }
  });
});

return messages;
