// Helper function to validate image URL
function isValidImageUrl(url) {
  if (!url) return false;

  const urlLower = url.toLowerCase().trim();

  const invalidKeywords = ['aparat', 'logo', 'icon', 'banner', 'avatar', 'placeholder'];
  const videoExtensions = ['.mp4', '.webm', '.avi', '.mov', '.flv', '.mkv', '.m4v', '.3gp'];
  const validImageExtensions = ['.jpg', '.jpeg', '.png', '.gif', '.webp', '.svg'];

  const hasVideoExtension = videoExtensions.some(ext => urlLower.includes(ext));
  const hasInvalidKeyword = invalidKeywords.some(k => urlLower.includes(k));
  const hasValidImageExtension = validImageExtensions.some(ext => urlLower.endsWith(ext));

  return !hasVideoExtension && !hasInvalidKeyword && hasValidImageExtension;
}

