function parseSrcset(srcsetString) {
  if (!srcsetString) return null;

  const pairs = [...srcsetString.matchAll(/(\S+)\s+(\d+)w/g)];
  if (pairs.length > 0) {
    pairs.sort((a, b) => parseInt(b[2]) - parseInt(a[2]));
    return pairs[0][1].trim();
  }
  return null;
}

