// Decode Link and Validate
const items = $input.all();
const validItems = [];

for (const item of items) {
  const json = item.json;

  // Get and validate link
  let link = json.link || '';
  link = link.trim();

  // Skip items without valid link
  if (!link || link.length < 10 || (!link.startsWith('http://') && !link.startsWith('https://'))) {
    console.log('Skipping item with invalid link:', link);
    continue;
  }

  // Decode URL-encoded characters
  try {
    link = decodeURIComponent(link);
  } catch (e) {
    // keep original if decode fails
  }

  // Keep original data + decoded link
  validItems.push({
    json: {
      ...json,
      link: link,
      originalTitle: json.title || 'بدون عنوان'
    }
  });
}

return validItems;

const items = $input.all();
const outputItems = [];

for (const item of items) {
  const json = item.json;

  // --- 1. استخراج داده‌ها ---
  const title = json.title || json.originalTitle || 'بدون عنوان';
  const link = json.link || '';
  let imageUrl = json.imageUrl || '';

  // --- 2. مدیریت محتوای متنی ---
  let contentString = '';
  if (Array.isArray(json.content)) {
    // اگر محتوا آرایه‌ای از پاراگراف‌ها بود، آن‌ها را با دو خط جدید به هم بچسبان
    contentString = json.content.join('\n\n');
  } else if (json.content) {
    // اگر رشته بود، همان را استفاده کن
    contentString = json.content;
  }

  // --- 3. اعتبارسنجی URL عکس ---
  if (imageUrl) {
    imageUrl = imageUrl.trim();
    try {
      if (imageUrl.includes('%')) {
        imageUrl = decodeURIComponent(imageUrl);
      }
    } catch (e) { /* نادیده گرفتن خطای decode */ }

    if (!imageUrl.startsWith('http://') && !imageUrl.startsWith('https://')) {
      imageUrl = '';
    }
  }

  // --- 4. ساخت خلاصه ---
  // 500 کاراکتر اول محتوا را به عنوان خلاصه برمی‌داریم
  let summary = contentString.substring(0, 500);
  if (contentString.length > 500) {
    summary += '...';
  }

  // --- 5. ساخت کپشن با فرمت جدید (HTML) ---
  // ❗️نام کانال خود را جایگزین @YourChannelName کنید
  let caption = `📰 <b>${title}</b>\n\n${summary}\n\n🔗 <a href="${link}">ادامه مطلب در وبسایت</a>\n\n📡 @w_e_s_h`;

  // --- 6. محدودسازی طول کپشن ---
  // تلگرام برای کپشن عکس محدودیت دارد (حدود 1024 کاراکتر)
  // ما برای اطمینان روی 950 کاراکتر محدود می‌کنیم
  if (caption.length > 950) {
    const overhead = title.length + link.length + 100; // طول متن‌های اضافه
    const maxSummaryLength = 950 - overhead;
    summary = contentString.substring(0, maxSummaryLength) + '...';

    // ساخت مجدد کپشن با خلاصه‌ی کوتاه‌تر
    caption = `📰 <b>${title}</b>\n\n${summary}\n\n🔗 <a href="${link}">ادامه مطلب در وبسایت</a>\n\n📡 @w_e_s_h`;
  }

  // --- 7. آماده‌سازی خروجی ---
  outputItems.push({
    json: {
      ...json, // ارسال همه داده‌های قبلی
      caption: caption,
      imageUrl: imageUrl,
      hasImage: Boolean(imageUrl), // فلگ مهم برای نود IF
      chatId: json.chatId || '-1003140416257' // استفاده از ChatID قبلی
    }
  });
}

return outputItems;
