// Decode Link and Validate
const items = $input.all();
const validItems = [];

for (const item of items) {
  const json = item.json;

  // Get and validate link
  let link = json.link || '';
  link = link.trim();

  // Skip items without valid link
  if (!link || link.length < 10 || (!link.startsWith('http://') && !link.startsWith('https://'))) {
    console.log('Skipping item with invalid link:', link);
    continue;
  }

  // Decode URL-encoded characters
  try {
    link = decodeURIComponent(link);
  } catch (e) {
    // keep original if decode fails
  }

  // Keep original data + decoded link
  validItems.push({
    json: {
      ...json,
      link: link,
      originalTitle: json.title || 'ÿ®ÿØŸàŸÜ ÿπŸÜŸàÿßŸÜ'
    }
  });
}

return validItems;

